import React, { Component } from 'react';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';
import { faEdit } from '@fortawesome/free-solid-svg-icons';


class LogComponent extends Component {
  state = {
    data: [],
    modalInsertar: false,
    form: {
      id_users: '',
      email: '',
      password: ''
    }
  }

  peticionGet = () => {
    axios.get("http://localhost:8080/user/GetUsers").then(response => {
      this.setState({ data: response.data });
    }).catch(error => {
      console.log(error.message);
    })
  }

  peticionPost = async () => {
    delete this.state.form.id_users;
    await axios.post("http://localhost:8080/login/FindUserByEmailAndPass", this.state.form).then(response => {
      console.log(response);
    })
  }

  peticionPut = () => {
    axios.put("http://localhost:8080/user/UpdateUser/" + this.state.form.id_users, this.state.form).then(response => {
      this.modalInsertar();
      this.peticionGet();
    })
  }


  modalInsertar = () => {
    this.setState({ modalInsertar: !this.state.modalInsertar });
  }

  seleccionarUser = (users) => {
    this.setState({
      tipoModal: 'actualizar',
      form: {
        id_users: users.id_users,
        email: users.email,
        password: users.password
      }
    })
  }

  handleChange = async e => {
    e.persist();
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    });
    console.log(this.state.form);
  }

  componentDidMount() {
    this.peticionGet();
  }


  render() {
    const { form } = this.state;
    return (
      <div className="App">
        <br /><br /><br />
        <button className="btn btn-success" onClick={() => { this.setState({ form: null, tipoModal: 'insertar' }); this.modalInsertar() }}>Login</button>
        <br /><br />
        <table className="table-user" id="table-user">
          <thead>
            <tr>
              <th>Email</th>
              <th>Password</th>
            </tr>
          </thead>
          <tbody>
            {this.state.data.map(users => {
              return (
                <tr>
                  <td>{users.email}</td>
                  <td>{users.password}</td>
                  <td>
                    <button className="btn btn-primary" onClick={() => { this.seleccionarUser(users); this.modalInsertar() }}><FontAwesomeIcon icon={faEdit} /></button>
                  </td>
                </tr>
              )
            })}
          </tbody>
        </table>



        <Modal isOpen={this.state.modalInsertar}>
          <ModalHeader style={{ display: 'block' }}>
            <span style={{ float: 'right' }} onClick={() => this.modalInsertar()}>x</span>
          </ModalHeader>
          <ModalBody>
            <div className="form-group">

            <label htmlFor="description">Email</label>
              <input className="form-control" type="email" name="email" id="email" onChange={this.handleChange} value={form ? form.email : ''} />
              <br />
              <label htmlFor="category">Password</label>
              <input className="form-control" type="password" name="password" id="password" onChange={this.handleChange} value={form ? form.password : ''} />
              
            </div>
          </ModalBody>

          <ModalFooter>
            {this.state.tipoModal == 'insertar' ?
              <button className="btn btn-success" onClick={() => this.peticionPost()}>
                LogIn
              </button> : <button className="btn btn-primary" onClick={() => this.peticionPut()}>
               Reset Password
              </button>
            }
            <button className="btn btn-danger" onClick={() => this.modalInsertar()}>Cancelar</button>
          </ModalFooter>
        </Modal>

      </div>



    );
  }
}

export default LogComponent;
