import NavComponent from "../components/NavComponent";
import UserComponent from "../components/UserComponent";
import ReactHTMLTableToExcel from 'react-html-table-to-excel';
import  { useParams } from "react-router-dom";
import { useContext, useEffect, useState } from "react";


const UserCont =  () => {


    const Agregar= (event, products)=>{
      carrito.push(products);
      setCarrito([...carrito]);
    }
    const handleKeyUp= (e)=> {
      console.log(e.target.value);
      const productFilter= products.filter(element =>{ 
        if (element.title.toUpperCase().match(e.target.value.toUpperCase())){
          return true
        }
        return false;
        
      })
      setProducts(productFilter);
    }

    return(
        <div className="container">
          <NavComponent/>
          <div className="btnexport">
<ReactHTMLTableToExcel
                    id="test-table-xls-button"
                    className="btn btn-outline-success"
                    table="table-user"
                    filename="User-List"
                    sheet="tableUsers"
                    buttonText="Download List"/>
          </div> 
         
                      <button className="btn btn-success mt-2" onClick={() => { this.setState({ form: null, tipoModal: 'insertar' }); this.modalInsertar() }}>Crear Usuario</button>
      
                       <UserComponent  Agregar={Agregar}/>
                     
        
      </div>
     
      
    )
}
    
export default UserCont;
